import React, { useEffect, useState } from 'react'

const Application = () => {
    const [infos,setInfos]=useState({field:'Technology',companyName:'IBM',title:'Software Engineer',appDate:"15/06/2024",status:2,interviewDate:""})
    const [style,setStyle]=useState({})
    useEffect(()=>{
        if(infos.status===1)
            setStyle({color:"green"})
        else if(infos.status===0)
            setStyle({color:"red"})
        else if(infos.status===2)
            setStyle({color:"gray"})
    },[infos])
  return (
    <div className='oneApp' id="application">
        <div className='infos'>
            <div>
                <p>{infos.field}</p>
                <h2>{infos.title}</h2>
                <h3>{infos.companyName}</h3>
            </div>
            <div className='appDate'>
                {
                    infos.status===1?<p style={style}>Interview on <br></br> {infos.interviewDate}</p>:<p style={style}>Applied on<br></br> {infos.appDate}</p>
                }
            </div>
        </div>
    </div>
  )
}

export default Application
