import React, { useState } from 'react'
import Input from '../Components/Input'
import { industries } from '../Controllers/utils'

const UpdateCandidate = () => {
  const [inputs,setInputs]=useState({})
  const handleSubmit=()=>{

  }
  const handleRadio=()=>{
    
  }
  return (
    <section className='sec update'>
      <form onSubmit={handleSubmit}>
        <Input
          name="firstName"
          inputs={inputs}
          setInputs={setInputs}
          type="text"
          placeholder="First Name"
        />
        <Input
          name="lastName"
          inputs={inputs}
          setInputs={setInputs}
          type="text"
          placeholder="Last Name"
        />
        <Input
          name="login"
          inputs={inputs}
          setInputs={setInputs}
          type="text"
          placeholder="Email"
        />
        <Input
          name="number"
          inputs={inputs}
          setInputs={setInputs}
          type="number"
          placeholder="Phone Number"
        />
        <div className='selIn'>
          <label>Field Of Interest : </label>
          <select name='field' onChange={(e)=>{setInputs((i)=>{return {...i,[e.target.name]:e.target.value}})}}>
              <option value="">Select An Industry</option>
              {
                  industries.map((industrie)=>{
                      return(
                          <option value={industrie}>{industrie}</option>
                      )
                  })
              }
          </select>
        </div>
        <div className='top'>
          <label>
              <input 
                  type='radio'
                  name='emailReceive'
                  value="Yes"
                  onChange={handleRadio}
                  id="yesRadio"
              />
              Yes
          </label>
          <label>
              <input 
                  type='radio'
                  name='emailReceive'
                  value="No"
                  onChange={handleRadio}
                  id="noRadio"
              />
              No
          </label>
        </div>
        <Input
          name="login"
          inputs={inputs}
          setInputs={setInputs}
          type="text"
          placeholder="Email"
        />
      </form>
    </section>
  )
}

export default UpdateCandidate
