const FeaturesCard = ({title,desc}) => {
    const handleMouseOn=()=>{
        document.getElementById(title+"face1").style.display="none"
        document.getElementById(title+"face2").style.display="block"
    }
    const handleMouseOut=()=>{
        document.getElementById(title+"face2").style.display="none"
        document.getElementById(title+"face1").style.display="block"
    }
    const animation=[
        {transform:""}
    ]
  return (
    <div className='featuresCard' onMouseEnter={handleMouseOn} onMouseLeave={handleMouseOut}>
        <div className='face1' id={`${title}face1`}>
            <h1>{title}</h1>
        </div>
        <div className='face2' id={`${title}face2`}>
            <p>{desc}</p>
        </div>
    </div>
  )
}

export default FeaturesCard
