import React, { useContext, useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'
import userContext from '../Controllers/userContext'

const Application = ({IdOffer,field,companyName,title,appDate,status,interviewDate,firstName,lastName}) => {
    const [style,setStyle]=useState({})
    const useCon=useContext(userContext)
    const navigate=useNavigate()
    const handleAccept=({})=>{

    }
    const handleRefuse=()=>{

    }
    useEffect(()=>{
        if(status===1)
            setStyle({color:"green"})
        else if(status===0)
            setStyle({color:"red"})
        else if(status===2)
            setStyle({color:"gray"})
    },[])
    let tmp=new Date(appDate)
    appDate=`${tmp.getDate()}/${tmp.getMonth()+1}/${tmp.getFullYear()}`
  return (
    <div className='oneApp'>
        <div className='infos' onClick={()=>{navigate('/offers/'+IdOffer)}}>
            <div>
                <p>{field}</p>
                <h2>{title}</h2>
                <h3>{companyName?companyName:`${firstName} ${lastName}`}</h3>
            </div>
            <div className='appDate'>
                {
                    status===1?<p style={style}>Interview on <br></br> {interviewDate}</p>:<p style={style}>Applied on<br></br> {appDate}</p>
                }
            </div>
        </div>
        {
            useCon.userType==="employer" && status===2?
            <div className='buttons'>
                <button style={{backgroundColor:'lightGreen'}} onClick={handleAccept}>Set Interview</button>
                <button onClick={handleRefuse}>Refuse</button>
            </div>:null
        }
    </div>
  )
}

export default Application
