const login=async(inputs,setError)=>{
    const response=await fetch(process.env.SERVER_URI+"",{
        method:"POST",
        credentials:'include',
        body:JSON.stringify(inputs)
    })
    await response.json().then((res)=>{
        setError(res.msg)
    })
}
const signup=async(inputs,setError)=>{
    const response=await fetch(process.env.SERVER_URI,{
        method:"POST",
        credentials:'include',
        body:JSON.stringify(inputs)
    })
    await response.json().then((res)=>{
        setError(res.msg)
    })
}

module.exports={
    signup,
    login
}
