import React, { useState,useEffect, useContext } from 'react'
import SearchBar from '../Components/SearchBar'
import Offer from '../Components/Offer'
import { industries, presence } from '../Controllers/utils'
import userContext from '../Controllers/userContext'
import Input from '../Components/Input'

const Offers = () => {
  const userCon=useContext(userContext)
  const [offers,setOffers]=useState({num:0,offers:[]});
  const [inputs,setInputs]=useState({});
  const handleSubmit=()=>{

  }
  return (
    <section className='sec offers'>
      <div className='all'>
        <SearchBar page="offers" setFunction={setOffers}/>
        <div className='resNum'>
          <p>{userCon.userType==="employer"?"Number of offers you posted":"Results"} : {offers.num}</p>
        </div>
        <div className='offerContainer'>
          {
            offers.num!==0?
            offers.offers.map((offer)=>{
              let date=new Date(offer.postDate)
              const dateToShow=`${date.getDay()}/${date.getMonth()+1}/${date.getFullYear()}`
              return(
                <Offer
                  infos={{
                    id:offer.id,
                    field:offer.field,
                    title:offer.title,
                    presence:offer.presence,
                    time:offer.time,
                    position:offer.position,
                    postDate:dateToShow,
                    num:offer.num
                  }}
                />
              )
            })
            :<h1 className='noData'>No Offers For The Moment</h1>
          }
        </div>
      </div>
      {
        userCon.userType==="employer"?
        <div className='add'>
            <div className='head'>
              <i class="fa-solid fa-plus"></i>
            </div>
            <div className='formHolder'>
              <form onSubmit={handleSubmit}>
                  <Input
                    name="title"
                    inputs={inputs}
                    setInputs={setInputs}
                    type="text"
                    placeholder="Title"
                  />
                  <div>
                    <label>Field : </label>
                    <select name="field">
                      <option value="">--Industry--</option>
                      {
                        industries.map((industry)=>{
                          return(
                            <option value={industry}>{industry}</option>
                          )
                        })
                      }
                    </select>
                  </div>
                  <div>
                    <textarea></textarea>
                  </div>
              </form>
            </div>
        </div>:null
      }
    </section>
  )
}

export default Offers
