const basicTemplate=async(path,objct,setFun)=>{
    const response=await fetch(process.env.REACT_APP_SERVER_URI+"Candidate/"+path,{
        method:"POST",
        credentials:'include',
        body:JSON.stringify(objct),
        headers:{
            "Content-Type":"application/json"
        }
    })
    await response.json().then((res)=>{
        setFun(res)
    })
}
const getOffers=async(filters,setOffers)=>{
    const response=await fetch(process.env.REACT_APP_SERVER_URI+"Candidate/offers",{
        method:"POST",
        credentials:'include',
        body:JSON.stringify(filters),
        headers:{
            "Content-Type":"application/json"
        }
    })
    await response.json().then((res)=>{
        setOffers(res)
    })
}

module.exports={
    getOffers,
    basicTemplate
}
